import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import { getDispatch } from "../services/api";

function Dispatch({ token }) {
  const [dispatchList, setDispatchList] = useState([]);
  const [date, setDate] = useState("2024-11-01"); // 기본 날짜 고정
  const navigate = useNavigate();

  useEffect(() => {
    // 토큰이 없으면 로그인 페이지로 리다이렉트
    if (!token) {
      alert("로그인 후 이용해주세요.");
      navigate("/");
    }
  }, [token, navigate]);

  // 날짜 유효성 검증
  const isValidDate = (selectedDate) => {
    const minDate = new Date("2024-11-01");
    const maxDate = new Date("2024-11-03");
    const currentDate = new Date(selectedDate);
    return currentDate >= minDate && currentDate <= maxDate;
  };


  const Dispatch_Check = async () => {
    if (!isValidDate(date)) {
      alert("날짜는 2024-11-01부터 2024-11-03 사이여야 합니다.");
      return;
    }

    try {
      const response = await axios.get(`${BASE_URL}/dispatch/driver/${date}`, {
        headers: {
          Authorization: `Bearer ${token}`, // JWT 토큰 헤더 추가
        },
      });

      console.log(response.data);

      // API 응답 데이터가 있는지 확인하고 상태 업데이트
      if (response.data.object && response.data.object.length > 0) {
        setDispatchList(response.data.object);
      } else {
        setDispatchList([]); // 데이터가 없으면 빈 배열로 설정
      }
    } catch (error) {
      console.error("배차 정보를 가져오는 데 실패했습니다.", error);
      alert("배차 정보를 가져오는 데 실패했습니다.");
    }
  };


  return (
    <div>
      <h1>배차 정보 페이지</h1>
      {/* 날짜 선택 */}
      <p>날짜는 2024-11-01부터 2024-11-03 사이여야 합니다.</p>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        min="2024-11-01"
        max="2024-11-03"
      />
      <button onClick={Dispatch_Check}>배차 정보 조회</button>

      {/* 현재 날짜 배차 정보 */}
      {dispatchList.length > 0 ? (
        <ul>
          {dispatchList.map((dispatch) => (
            <div>
            <li key={dispatch.id}>
              <strong>버스 번호:</strong> {dispatch.busNumber}
              <br></br>
              <strong>실제 운행 시작 시각:</strong> {dispatch.startTime}
            </li>
            <br></br>
            </div>
          ))}
        </ul>
      ) : (
        <p>배차일보가 존재하지 않습니다.</p>
      )}
    </div>
  );
}

export default Dispatch;
