import React, { useEffect, useState } from "react";
import { getDispatch, getNotAssignedDispatch } from "../services/api"; // 추가된 API 가져오기

function Dispatch({ token }) {
  const [dispatchList, setDispatchList] = useState([]);
  const [notAssignedList, setNotAssignedList] = useState([]); // 소속되지 않은 배차 정보 상태 추가
  const [date, setDate] = useState("");
  const [showNotAssigned, setShowNotAssigned] = useState(false); // 소속되지 않은 배차 표시 상태

  // 현재 날짜의 배차 정보 조회
  const handleFetchDispatch = async () => {
    try {
      const response = await getDispatch(token, date);
      setDispatchList(response.data); // 배차 데이터 저장
    } catch (error) {
      console.error("Failed to fetch dispatch data", error);
      alert("Failed to fetch dispatch data. Please check the date and try again.");
    }
  };

  // 소속되지 않은 배차 정보 조회
  const handleFetchNotAssignedDispatch = async () => {
    try {
      const response = await getNotAssignedDispatch(token, date);
      setNotAssignedList(response.data); // 소속되지 않은 배차 데이터 저장
      setShowNotAssigned(true); // 소속되지 않은 배차 정보 표시
    } catch (error) {
      console.error("Failed to fetch not-assigned dispatch data", error);
      alert("Failed to fetch not-assigned dispatch data. Please try again.");
    }
  };

  return (
    <div>
      <h1>Dispatch Page</h1>
      {/* 날짜 선택 */}
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <button onClick={handleFetchDispatch}>Fetch Dispatch</button>

      {/* 현재 날짜 배차 정보 */}
      {dispatchList.length > 0 ? (
        <ul>
          {dispatchList.map((dispatch) => (
            <li key={dispatch.id}>
              <strong>Bus Number:</strong> {dispatch.busNumber} | 
              <strong> Start Time:</strong> {dispatch.actualStartTime}
            </li>
          ))}
        </ul>
      ) : (
        <p>No dispatch data available.</p>
      )}

      {/* 소속되지 않은 배차 정보 조회 버튼 */}
      <button onClick={handleFetchNotAssignedDispatch}>Show Not Assigned Dispatch</button>

      {/* 소속되지 않은 배차 정보 */}
      {showNotAssigned && (
        <>
          <h2>Not Assigned Dispatch</h2>
          {notAssignedList.length > 0 ? (
            <ul>
              {notAssignedList.map((dispatch) => (
                <li key={dispatch.id}>
                  <strong>Bus Number:</strong> {dispatch.busNumber} | 
                  <strong> Start Time:</strong> {dispatch.actualStartTime}
                </li>
              ))}
            </ul>
          ) : (
            <p>No not-assigned dispatch data available.</p>
          )}
        </>
      )}
    </div>
  );
}

export default Dispatch;
