import React, { useState, useEffect } from "react";
import { getDispatch, getNotAssignedDispatch } from "../services/api";

function Dispatch({ token }) {
  const [date, setDate] = useState("2024-11-01"); // 날짜 고정
  const [dispatchList, setDispatchList] = useState([]);
  const [notAssignedList, setNotAssignedList] = useState([]);
  const [showNotAssigned, setShowNotAssigned] = useState(false);

  // 배차 정보 조회
  const fetchDispatch = async () => {
    try {
      const response = await getDispatch(token, date);
      setDispatchList(response.data); // 배차 데이터 설정
    } catch (error) {
      console.error("Failed to fetch dispatch data:", error);
      alert("Failed to fetch dispatch data. Please try again.");
    }
  };

  // 소속되지 않은 배차 정보 조회
  const fetchNotAssignedDispatch = async () => {
    try {
      const response = await getNotAssignedDispatch(token, date);
      setNotAssignedList(response.data); // 소속되지 않은 배차 데이터 설정
      setShowNotAssigned(true);
    } catch (error) {
      console.error("Failed to fetch not-assigned dispatch data:", error);
      alert("Failed to fetch not-assigned dispatch data. Please try again.");
    }
  };

  useEffect(() => {
    fetchDispatch(); // 페이지 로드 시 배차 정보 조회
  }, [date]);

  return (
    <div>
      <h1>Dispatch Page</h1>
      <p>Current Date: {date}</p>

      <h2>Assigned Dispatch</h2>
      {dispatchList.length > 0 ? (
        <ul>
          {dispatchList.map((item) => (
            <li key={item.id}>
              <strong>Bus Number:</strong> {item.busNumber}
            </li>
          ))}
        </ul>
      ) : (
        <p>No dispatch data available for the selected date.</p>
      )}

      <button onClick={fetchNotAssignedDispatch}>
        Show Not Assigned Dispatch
      </button>

      {showNotAssigned && (
        <>
          <h2>Not Assigned Dispatch</h2>
          {notAssignedList.length > 0 ? (
            <ul>
              {notAssignedList.map((item) => (
                <li key={item.id}>
                  <strong>Bus Number:</strong> {item.busNumber}
                </li>
              ))}
            </ul>
          ) : (
            <p>No not-assigned dispatch data available.</p>
          )}
        </>
      )}
    </div>
  );
}

export default Dispatch;
