import React, { useState, useEffect } from "react";
import { getDispatch } from "../services/api";

function Dispatch({ token }) {
  const [dispatchList, setDispatchList] = useState([]);
  const [notAssignedDispatch, setNotAssignedDispatch] = useState([]);
  const [date, setDate] = useState(getTodayDate()); // 기본값은 오늘 날짜
  const [showNotAssigned, setShowNotAssigned] = useState(false);

  // 오늘 날짜를 가져오는 유틸리티 함수
  function getTodayDate() {
    const today = new Date();
    return today.toISOString().split("T")[0]; // YYYY-MM-DD 형식
  }

  // 배차 데이터 조회
  const fetchDispatchData = async () => {
    try {
      const response = await getDispatch(token, date);
      setDispatchList(response.data); // 배차 데이터 설정
    } catch (error) {
      console.error("Failed to fetch dispatch data", error);
      alert("Failed to fetch dispatch data. Please try again.");
    }
  };

  // 소속되지 않은 배차 데이터 조회
  const fetchNotAssignedDispatch = async () => {
    try {
      const response = await getDispatch(token, `${date}/not-assigned`);
      setNotAssignedDispatch(response.data); // 소속되지 않은 배차 데이터 설정
    } catch (error) {
      console.error("Failed to fetch not-assigned dispatch data", error);
      alert("Failed to fetch not-assigned dispatch data. Please try again.");
    }
  };

  // 데이터 조회 버튼 핸들러
  const handleFetchData = () => {
    fetchDispatchData();
  };

  // 소속되지 않은 배차 조회 버튼 핸들러
  const handleShowNotAssigned = () => {
    fetchNotAssignedDispatch();
    setShowNotAssigned(true);
  };

  return (
    <div>
      <h1>Dispatch Page</h1>

      {/* 날짜 선택 */}
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <button onClick={handleFetchData}>Fetch Dispatch</button>

      {/* 배차 정보 */}
      <h2>Today's Dispatch</h2>
      {dispatchList.length > 0 ? (
        <ul>
          {dispatchList.map((dispatch) => (
            <li key={dispatch.id}>
              <strong>Bus Number:</strong> {dispatch.busNumber} |
              <strong> Start Time:</strong> {dispatch.actualStartTime}
            </li>
          ))}
        </ul>
      ) : (
        <p>No dispatch data available for today.</p>
      )}

      {/* 소속되지 않은 배차 정보 */}
      <button onClick={handleShowNotAssigned}>Show Not Assigned Dispatch</button>
      {showNotAssigned && (
        <div>
          <h2>Not Assigned Dispatch</h2>
          {notAssignedDispatch.length > 0 ? (
            <ul>
              {notAssignedDispatch.map((dispatch) => (
                <li key={dispatch.id}>
                  <strong>Bus Number:</strong> {dispatch.busNumber} |
                  <strong> Start Time:</strong> {dispatch.actualStartTime}
                </li>
              ))}
            </ul>
          ) : (
            <p>No not-assigned dispatch data available.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Dispatch;
