import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getDispatch, getNotAssignedDispatch } from "../services/api";

function Dispatch({ token }) {
  const [dispatchList, setDispatchList] = useState([]);
  const [notAssignedList, setNotAssignedList] = useState([]);
  const [date, setDate] = useState("2024-11-01"); // 기본 날짜 고정
  const [showNotAssigned, setShowNotAssigned] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // 토큰이 없으면 로그인 페이지로 리다이렉트
    if (!token) {
      alert("로그인 후 이용해주세요.");
      navigate("/");
    }
  }, [token, navigate]);

  // 날짜 유효성 검증
  const isValidDate = (selectedDate) => {
    const minDate = new Date("2024-11-01");
    const maxDate = new Date("2024-11-03");
    const currentDate = new Date(selectedDate);
    return currentDate >= minDate && currentDate <= maxDate;
  };

  const handleFetchDispatch = async () => {
    if (!isValidDate(date)) {
      alert("날짜는 2024-11-01부터 2024-11-03 사이여야 합니다.");
      return;
    }

    try {
      const response = await getDispatch(token, date);
      setDispatchList(response.data);
    } catch (error) {
      console.error("배차 정보를 가져오는 데 실패했습니다.", error);
      alert("배차 정보를 가져오는 데 실패했습니다. 날짜를 확인 후 다시 시도해주세요.");
    }
  };

  const handleFetchNotAssignedDispatch = async () => {
    if (!isValidDate(date)) {
      alert("날짜는 2024-11-01부터 2024-11-03 사이여야 합니다.");
      return;
    }

    try {
      const response = await getNotAssignedDispatch(token, date);
      setNotAssignedList(response.data);
      setShowNotAssigned(true);
    } catch (error) {
      console.error("소속되지 않은 배차 정보를 가져오는 데 실패했습니다.", error);
      alert("소속되지 않은 배차 정보를 가져오는 데 실패했습니다.");
    }
  };

  return (
    <div>
      <h1>배차 정보 페이지</h1>
      {/* 날짜 선택 */}
      <p>안내: 날짜는 2024-11-01부터 2024-11-03 사이만 조회 가능합니다.</p>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        min="2024-11-01"
        max="2024-11-03"
      />
      <button onClick={handleFetchDispatch}>배차 정보 조회</button>

      {/* 현재 날짜 배차 정보 */}
      {dispatchList.length > 0 ? (
        <ul>
          {dispatchList.map((dispatch) => (
            <li key={dispatch.id}>
              <strong>버스 번호:</strong> {dispatch.busNumber} | 
              <strong> 시작 시간:</strong> {dispatch.actualStartTime}
            </li>
          ))}
        </ul>
      ) : (
        <p>선택한 날짜에 배차 정보가 없습니다.</p>
      )}

      <button onClick={handleFetchNotAssignedDispatch}>소속되지 않은 배차 정보 조회</button>
      {showNotAssigned && (
        <>
          <h2>소속되지 않은 배차 정보</h2>
          {notAssignedList.length > 0 ? (
            <ul>
              {notAssignedList.map((dispatch) => (
                <li key={dispatch.id}>
                  <strong>버스 번호:</strong> {dispatch.busNumber} | 
                  <strong> 시작 시간:</strong> {dispatch.actualStartTime}
                </li>
              ))}
            </ul>
          ) : (
            <p>소속되지 않은 배차 정보가 없습니다.</p>
          )}
        </>
      )}
    </div>
  );
}

export default Dispatch;
