import React, { useEffect, useState } from "react";
import { getDriverInfo } from "../services/api";

function Main({ token }) {
  const [driverInfo, setDriverInfo] = useState(null);

  useEffect(() => {
    const fetchDriverInfo = async () => {
      try {
        const response = await getDriverInfo(token);
        setDriverInfo(response.data.object);
      } catch (error) {
        console.error("Failed to fetch driver info", error);
        alert("Failed to fetch driver information. Please try again.");
      }
    };

    fetchDriverInfo();
  }, [token]);

  return (
    <div>
      <h1>Main Page</h1>
      {driverInfo ? (
        <div>
          <p><strong>Driver Name:</strong> {driverInfo.driverName}</p>
          <p><strong>Route Name:</strong> {driverInfo.routeName}</p>
          <p><strong>Bus Number:</strong> {driverInfo.busNumber}</p>
        </div>
      ) : (
        <p>Loading driver information...</p>
      )}
    </div>
  );
}

export default Main;
