import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getDriverInfo } from "../services/api";
import "/css/Main.css";

function Main({ token }) {
  const [driverInfo, setDriverInfo] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDriverInfo = async () => {
      try {
        const response = await getDriverInfo(token);
        setDriverInfo(response.data.object);
      } catch (error) {
        console.error("Failed to fetch driver info", error);
        alert("Failed to fetch driver information. Please try again.");
      }
    };

    fetchDriverInfo();
  }, [token]);

  const Main_Logout = () => {
    localStorage.removeItem("jwt");
    alert("로그아웃 성공!");
    navigate("/");
  };



  return (
    <div>
      <h1>Main Page</h1>
      {driverInfo ? (
        <div>
          <p><strong>Driver Name:</strong> {driverInfo.driverName}</p>
          <p><strong>Route Name:</strong> {driverInfo.routeName}</p>
          <p><strong>Bus Number:</strong> {driverInfo.busNumber}</p>
        </div>
      ) : (
        <p>Loading driver information...</p>
      )}
      {/* 로그아웃 버튼 추가 */}
      <button onClick={Main_Logout} style={{ marginTop: "20px", padding: "10px 20px", fontSize: "16px" }}>
        Logout
      </button>

      <button onClick={() => navigate("/dispatch")} style={{ marginTop: "20px", padding: "10px 20px", fontSize: "16px" }}>
        Bus
      </button>

    </div>
  );
}

export default Main;
